    public int getScaleValue(String value) {
        int scale;
        switch (value) {
            // case statements
            // values must be of same type of expression
            case "100":
                // Statements
                scale = 1;
                break;

            case "125":
                scale = 2;
                break;

            case "150":
                scale = 3;
                break;

            case "175":
                scale = 4;
                break;

            case "200":
                scale = 5;
                break;
            default:
                scale = 1;
                break;
        }
        return scale;
    }

